/* AGENT_SHEET */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
/*
 _____________________________________________________________________________
|                  |                                                          |
|          Title:  |  mDrifus - Firefox Aero Glass Project                    |
|    Description:  |  Aero Theme                                              |
|       Author's:  |  Drifus,Rist                                             |
|   Date Created:  |  15.11.2010                                              |
|   Last Updated:  |  10.03.2012                                              |
|        Version:  |  0.0.0.19                                                |
|__________________|__________________________________________________________|

*/
/*change the ugly throbber*/
.tab-throbber[progress] {
	list-style-image: url("chrome://browser/skin/places/searching_16.png") !important;
}

/*Hide the Search Engine image*/
.searchbar-engine-image {
	display: none !important;
}

/*Hide the search button (magnifying glass)*/
.search-go-button {
	display: none !important;
}

/*Hide bookmark icons in the Personal Toolbar*/
toolbarbutton.bookmark-item > .toolbarbutton-icon {
	display: none !important;
}

/*Hide urlbar history dropmarker*/
.urlbar-history-dropmarker {
	display: none !important;
}

/*Reduce titlebar margin - choose margin according your windows theme (minimize, maximize close buttons)*/
#main-window {
	margin-top: 0 !important;
}
#main-window[sizemode="normal"] #navigator-toolbox[tabsontop="true"] #TabsToolbar,#main-window[sizemode="normal"] #appmenu-button-container {
    padding-left: 3px !important;
}

#TabsToolbar[tabsontop="true"] {
	background: none !important;
	margin-bottom: 0 !important;
	height: 23px !important;
}

#TabsToolbar[tabsontop="false"] {
	background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.1)) !important;
	background-color: transparent !important;
	border: none !important;
	margin-bottom: 0 !important;
	padding: 0 !important;
}

#navigator-toolbox::after {
	height: 0 !important;
	border: none !important;
}

#main-window[sizemode="maximized"] {
	margin-top: -2px !important;
}

#main-window[sizemode="fullscreen"] {
	margin-top: 0 !important;
}

/*remove background of the combined go/reload/stop button in location bar*/
#urlbar > toolbarbutton {
	border-left: none !important;
	border-radius: 0 !important;
	padding-left: 2px !important;
}
#urlbar-go-button, #urlbar-go-button:hover, #urlbar-reload-button, #urlbar-reload-button:hover, #urlbar-stop-button, #urlbar-stop-button:hover {
	background-image: none !important;
	box-shadow: none !important;
}

/*Transparency, hover and more naked UI*/
#nav-bar {
	background-image: -moz-linear-gradient(rgba(255,255,255,.5), rgba(255,255,255,.1)) !important;
	background-color: transparent !important;
	border: none !important;
	margin-top: 0 !important;
	margin-bottom: 0 !important;
	padding: 0 !important;
}

	/* CTR small icons fix */
#main-window[defaultfxtheme="true"] #nav-bar #urlbar {
	border: none !important;
	}

#urlbar {
	background: none !important;
	border: none !important;
	border-radius: 3.5px 0 0 0 !important;
	box-shadow: none !important;
}
#searchbar > *, #search-box {
	background: none !important;
	border: none !important;
	border-radius: 3.5px 0 0 0 !important;
	margin: 0 !important;
	padding-left: 4px !important;
	box-shadow: none !important;
	height: 24px !important;
}
#urlbar:hover {
	background: -moz-linear-gradient(left, rgba(255,255,255,.5), rgba(255,255,255,.0)) !important;
}
#searchbar:hover > *, .findbar-textbox:hover {
	background: -moz-linear-gradient(left, rgba(255,255,255,.4), rgba(255,255,255,.0)) !important;
}
.urlbar-over-link-box {
	color: #222222 !important;
}
#PersonalToolbar {
	background-color: rgba(255,255,255,.1) !important;
	border: none !important;
	margin-top: 0 !important;
	margin-bottom: 0 !important;
	padding: 0 !important;
}
.tabbrowser-tab:not([selected="true"]) {
	background-image: -moz-linear-gradient(rgba(0,0,0,.1), rgba(0,0,0,.4)) !important;
	color: #dddddd !important;
	text-shadow: none !important;
}
.tabbrowser-tab:not([selected="true"]):hover {
	background-image: -moz-linear-gradient(rgba(0,0,0,.2), rgba(0,0,0,.5)) !important;
}
.tabbrowser-tab{
	margin-top: -1px !important;}

.tabbrowser-tab[selected="true"] {
	background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.5)) !important;
	text-shadow: none !important;
}
.tabbrowser-tab[pinned] {
	height: 23px !important;
}

  /*CTR new tabbutton*/
  #main-window #navigator-toolbox #TabsToolbar #tabbrowser-tabs .tabs-newtab-button:not(:-moz-lwtheme) {
    background-image: none!important;
			background-color: transparent !important;
  }
  #main-window #navigator-toolbox #TabsToolbar #tabbrowser-tabs .tabs-newtab-button:hover:not(:-moz-lwtheme) {
    background-image: -moz-linear-gradient(rgba(0,0,0,.2), rgba(0,0,0,.5)) !important;
  }

.tabs-newtab-button {
	background: none !important;
	border: none !important;
}

.tabs-newtab-button:hover, #new-tab-button:hover, .scrollbutton-down:hover, .scrollbutton-up:hover, #alltabs-button, #tabview-button:hover, #TabsToolbar #feed-button {
	-moz-appearance: none !important;
	background: none !important;
	box-shadow: none !important;
	border: none !important;
}
.toolbarbutton-1 > .toolbarbutton-menubutton-button, .toolbarbutton-1 > .toolbarbutton-menubutton-dropmarker, .toolbarbutton-1 {
	background: none !important;
	box-shadow: none !important;
	border-color: transparent !important;
}

.toolbarbutton-1 > .toolbarbutton-menubutton-button:not([disabled="true"]):not(:active):hover, .toolbarbutton-1:not([open="true"]):not(:active):hover > .toolbarbutton-menubutton-dropmarker:not([disabled="true"]),  .toolbarbutton-1:not([type="menu-button"]):not([disabled="true"]):not([checked="true"]):not([open="true"]):not(:active):hover{
	background-color: hsla(190,60%,70%,.5) !important;
	border-color: hsla(190,50%,65%,.8) hsla(190,50%,50%,.8) hsla(190,50%,40%,.8) !important;
	box-shadow: 0 0 0 1px rgba(255,255,255,.3) inset, 0 0 0 1.5px rgba(255,255,255,.1) inset, 0 1px 0 rgba(0,0,0,.1), 0 0 3.5px hsl(190,90%,80%) !important;
	-moz-transition: background-color .4s ease-in,  border-color .2s ease-in,  box-shadow .2s ease-in !important;
	border-radius: 6.5px !important;
}

#navigator-toolbox:not([iconsize="large"]) #nav-bar .toolbarbutton-1 {
-moz-appearance: none !important;
}



#identity-box:not(.verifiedIdentity):not(.verifiedDomain) {
	background: none !important;
	box-shadow: none !important;
	border-color: transparent !important;
}

/*Findbar*/
findbar {
	background: transparent !important;
	margin: 0 !important;
	box-shadow: none !important;
	background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.1)) !important;
}

/*Addon bar*/
#browser-bottombox {
  border: none !important;
	background: transparent !important;
	background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.3)) !important;
	margin-top: -1px !important;
}

#addon-bar,#downbarHolder,#s3downbar_holder,#FindToolbar{
	border: none !important;
	background: transparent !important;	
}
/*icons*/
#navigator-toolbox :-moz-any(#back-button, #forward-button,#reload-button,#stop-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button:not([status="active"]), #feed-button) > .toolbarbutton-icon {
	list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important;
}

#main-window #alltabs-button {
	list-style-image: url("chrome://browser/skin/mainwindow-dropdown-arrow-inverted.png") !important;
}
#main-window #tabview-button {
	list-style-image: url("chrome://browser/skin/tabview/tabview-inverted.png") !important;
}
#main-window .tabs-newtab-button, #main-window #TabsToolbar #new-tab-button > .toolbarbutton-icon {
	list-style-image: url("chrome://browser/skin/tabbrowser/newtab-inverted.png") !important;
}
#main-window .scrollbutton-up, #main-window .scrollbutton-down {
	list-style-image: url("chrome://browser/skin/tabbrowser/tab-arrow-left-inverted.png") !important;
}
/* Menu Button Fixes */ 

#appmenu-button
{   border: 0.5px solid rgba(90,30,0,0.8)!important;
    background: rgba(255,255,255,.1) !important;
    border-top:none!important;
    padding: 2px 5px 1px 5px !important;
    height: 23px !important;
    min-width: 43px !important;
    width: 43px !important; }

#appmenu-button:hover, #appmenu-button[open]
{       background-image: -moz-linear-gradient(rgba(2,1,10,.2), rgba(2,1,10,.2) 100%, transparent 100%), -moz-radial-gradient(14px, circle cover, rgba(255,255,255,.1) 100%, rgba(255,255,255,.1) 100%, rgba(255,255,255,.1) 100%) !important;  }

#appmenu-button .button-text
{ 
    color: transparent !important;
    text-shadow: none !important;
    margin-left: -27px !important;
    margin-right: -33px !important;
    background: url("data:image/png;base64,") no-repeat 16px !important; }

#appmenu-button dropmarker
{  margin: 0 1px 0 0 !important; }

#main-window[sizemode="normal"][tabsontop="true"] #appmenu-button-container 
{    position: fixed !important; 
	margin-top: -3px !important;
}

#main-window[sizemode="maximized"][tabsontop="true"] #appmenu-button-container 
{    position: fixed !important; 
     margin: 0px 0px 0px 2px !important;
}

/* sidebar,verticalbar,xFiretoolbar aero */

.sidebar-placesTreechildren { 
	color: white !important; /* <-Siderbar text color */
	font-size: 95% !important;}/* Sidebar text size*/

treechildren::-moz-tree-row { 
    height: 15px !important; /*Sidebar row height*/
    min-height : 0    !important;
    min-width : 0    !important;}
    
treechildren::-moz-tree-image { /*Sidebar icon size*/
	width: 14px !important;
	height: 14px !important;}


#bookmarksPanel,#history-panel,
.sidebar-placesTree,#browser-border-start,#browser-border-end
 {
	background-color: transparent !important;
	-moz-appearance: -moz-win-glass !important;
	border: none !important;
	margin-left: -1px !important;

}

#xfirexo,#vertical-toolbox,#sidebar
 {
	background-image: -moz-linear-gradient(rgba(255,255,255,.1), rgba(255,255,255,.1)) !important;
	background-color: transparent !important;
	-moz-appearance: none !important;
	border: none !important;
	min-width : 30px !important;/*Sidebar minimum width*/
}

#appcontent{
	-moz-appearance: none !important;
}

#sidebar-splitter {
	background-color: transparent !important;
	border: none !important;
}

/* Aero Scrollbars */

scrollbar, scrollbar thumb { 
-moz-appearance: none !important;
background-color: transparent !important;
background-image: none !important;
border: none !important; 
} 

scrollbar, scrollcorner { 
    background: transparent !important;
    background-color: rgba(255,255,255,.1) !important; 
} 

scrollbar thumb { 
    -moz-appearance: none !important;
    border-radius: 5px !important; 
    background: -moz-radial-gradient(center, rgba(50,50,50,.5), rgba(200,200,200,.7)) !important; 
}  

scrollbar:hover > * *{
background: -moz-radial-gradient(center, rgba(50,100,150,.5), rgba(150,200,255,.5)) !important;
}   
scrollbar[orient="vertical"] { min-width: 12px !important; max-width: 12px !important; } 
scrollbar[orient="horizontal"] { min-height: 12px !important;  max-width: 12px !important;} 
scrollbar scrollbarbutton { display: none ! important; }

/*Sidebar search*/

#bookmarksPanel,
#history-panel
{ 
-moz-box-direction: reverse;
}

label[value='Search:']
{
margin-left: 1px !important;
}

#bookmarksPanel
{
margin: 0px 0px 0px -15px !important;
}

#search-box
{
margin: 0px 0px 0px 11px !important;
}

#viewButton 
{
margin-left: -4px !important;
margin-right: 0px !important;
}

#bookmarksPanel label[control="search-box"],
#history-panel label[control="search-box"],#viewButton > hbox > hbox > label {
display: none !important;
}

/*Hide sidebar header*/
sidebarheader
{
  display: none !important;
}

/*iMacro Sidebar*/
#im-tabs,.tabs-right,#Recorder,#treeview,#boxlistbox *,#replaystat,#im-tabpanels,#im-play-button-decoration,#im-record-button-decoration
{
	background-color: transparent !important;
	color: white /*<--custom color*/!important;
	text-shadow: 0px 0px 6px white, 0px 0px 6px white, 0px 0px 6px white !important;
	-moz-appearance: -moz-win-glass !important;
}
/* About blank:Aero - opacity: 0.1 = aero*/
#content,
#appcontent,
#content > tabbox > tabpanels {
  background-color: transparent !important;
}

#main-window[inFullscreen="true"]{
  -moz-appearance: -moz-win-borderless-glass !important;
  background: none !important;
}

#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="View Source Chart"]) #content browser:not([src]){ 
opacity: 0.1 !important;
}
#_dlbar_progTipContent,#_dlbar_progTip,#_dlbar_finTip,#_dlbar_finTipContent{
    border: none !important;
    -moz-appearance: -moz-win-glass !important;
    background: transparent !important;
    text-shadow: 0px 0px 6px white, 0px 0px 6px white, 0px 0px 6px white !important;
}

#appmenuSecondaryPane,#appmenu-popup,menuseparator{

    border: none !important;
    -moz-appearance: -moz-win-glass !important;
    background: transparent !important;
    text-shadow: 0px 0px 8px white, 0px 0px 8px white, 0px 0px 8px white !important;
}
