/* SERVICES WHICH WILL SHOW CONTENT IN SEGOE UI */

@-moz-document url-prefix("https://www.google.com/accounts"),
               url("https://mail.google.com/mail/?shva=1"),
               url("https://mail.google.com/mail/?ui=2&ik=97e985c52b&view=ac"),
               domain("mail.google.com"),
               domain("googleusercontent.com") {

/* Universal Segoe UI Font */

    * { 
        font-family: "Segoe UI" !important;
    }
}
