style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tjetter.nl") {
  body {
    background: #333 !important;
  }
  * {
    border-color: #0078ff !important;
  }
  div[style='margin: 7px 10px 0pt 0pt; border: 1px solid rgb(196, 196, 198); background: url("/d/1/i/inputlinebg.png") no-repeat scroll left top rgb(255, 255, 255); padding: 2px 0pt 2px 2px;'] {
    -moz-border-radius: 10px;
  }
  td { 
    background: none !important; 
  }
  div[style="width: 960px;"] {
    background: none;
    width: 700px !important;
  }
  td[style="text-align: right; background-color: rgb(225, 225, 225); width: 200px; padding: 10px 10px 0pt;"] {
    display:none;
  }
  .chat-container {
    background: #bbb !important;
    -moz-border-radius: 10px;
  }
  a, .link, .from-other {
    color: #0078ff !important;
    font-weight: normal !important;
  }
  .from-one {
     color: #333 !important;
     font-weight: normal !important;
  }
  div[style="padding: 0pt 3px; position: absolute; right: 25px; font-weight: bold; top: 334px; background-color: white; color: rgb(0, 160, 0);"] {
     background: #333 !important;
     -moz-border-radius: 5px;
  }
  div[style="padding: 0pt 3px; position: absolute; right: 25px; font-weight: bold; top: 334px; background-color: white; color: rgb(0, 160, 0);"] span {
     color: #fff !important;
     padding: 2px;
  }
  div[style="padding-left: 65px; padding-right: 74px; text-align: justify; color: gray;"],
  .icon {
    display: none;
  }
}
