style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Firefox Stylish CSS for The (Daily) Telegraph    */
/* Restores "classic" (pre-Nov 2010) appearance     */
/* - no black borders, sans-serif font              */
/* Version 1.0 22/Nov/2010                          */

@-moz-document domain("telegraph.co.uk") {

 /* main body */
 body {
     /* get rid of black left- and right-hand borders */
     background:#ffffff!important;
     /* set default font to sans-serif */
     font-family:sans-serif!important;
 }


 /* these styles had specific font settings, so set them all to sans-serif */
 #tmglHotTopics ul li a { font-family:sans-serif!important; }
 #tmglPageTitle h1      { font-family:sans-serif!important; }

 .comment ul li a, .commentImg ul li a { font-family:sans-serif!important; 
                                         background:#ffffff!important; }
 .story .byline .bylineBody    { font-family:sans-serif!important; }
 .related_links_inline ul li a { font-family:sans-serif!important; }
 .picturesAndVideo .slideshow .imageLinks h3 { font-family:sans-serif!important; 
                                               background:#ffffff!important;
                                               font-size:12px!important;
                                               line-height:1.2em!important; }

 
 /* tidy up some side bar text */
 .summaryMedium h3                     { font-size:1.5em!important; }
 #mostPopular #mostpop .lists ol li h3 { font-size:1.0em!important; }

 /* adjust top-off-page navigation bars */
 #tmglSecondNav  .mainNav a {
     font-size:0.9em!important;
     font-weight:bold!important;
 }
 #tmglPrimaryNav .mainNav a {
     font-size:1.1em!important;
     font-weight:bold!important;
 }

}
