style.css:
a { text-decoration:underline!important; }
body,span,div,table,tr,th,td,ul,li,h1,h2,h3,h4,h5,h6,dd,dt,dl,p,fieldset,form {
  background-image:none!important;
}
body,span,div,table,tr,th,td,ul,li,a,h1,h2,h3,h4,h5,h6,dd,dt,dl,p,fieldset,form {
  background-color:#000!important;
  border-color:#222!important;
  -webkit-box-shadow:none!important;
  color:#999!important;
}
td,span,div,p,a,li,h1,h2,h3,h4,h5,h6,font,label,cite,th {
  color:#999!important;
  text-shadow:none!important;
}
img {
  opacity:0.6!important;
  border:none!important;
}
input,textarea,select,button {
  background-image:none!important;
  background-color:#333!important;
  color:#ccc!important;
  border:none!important;
}
hr {
  display:none!important;
}
