@-moz-document domain("mainichi.jp") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#WrappingLiquid
, #WrappingLiquid div
, #WrappingFix
, #WrappingFix div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#WrappingLiquid *
, #WrappingFix * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#WrappingLiquid a:link
, #WrappingFix a:link { color : #0000ee !important; }
#WrappingLiquid a:visited
, #WrappingFix a:visited { color : #551a8b !important; }
#WrappingLiquid a:hover
, #WrappingFix a:hover{ color : #ee0000 !important; }
#WrappingLiquid a:hover
, #WrappingFix a:hover{ background-color : transparent !important; }
/* /destroy div layout */

#Header
, #Footer
, .BrClr
, .Ad
, .Crumb
, .Article-Head
, .NewsIco
, .AdList
, .Amazon
, #SubColumn
, .FootLink
, .FootGenreNavi
, .AdChumoku
, .AdAA
, .Mark li img
, .MidashiList td img
, .Top-Head
{ display : none !important; }

.Mark li
, .MidashiList td
{ padding : 0 !important; }

.Mark li
{ margin : 0 !important; }

}
