style.css:
/* enable aero glass for rikaichan                */
/* ver: 1.0 (12-01-2010)                          */
/* 2010 xat - contact: xatnys[at]gmail[dot]com    */
/* this has been tested on Firefox 3.6            */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url-prefix('chrome://') {

	#rikaichan-lbar { 
		-moz-appearance: -moz-win-glass !important;
		background: black !important;
	}
	#rikaichan-lbar-text {
		opacity: 0.99 !important; /* hack to prevent text from becoming transparent */
	}

}
