style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("positech.co.uk") {

*{   
    color: #aaa !important;
    border-color: #111 !important;

}
body, html{background: #333 none repeat !important;}

Tr, th,.headerbar,.navbar,.forabg,.forumbg,.panel,span.corners-top,span.corners-top span,span.corners-bottom,span.corners-bottom span
,#tabs a,#tabs a span,#tabs a:hover span,#tabs .activetab a,#tabs .activetab a span
,#navigation a,.rtl #navigation a,#navigation #active-subsection a
{background: #151515 none repeat !important;}

td,form,.ucprowbg,.fieldsbg,a.forumtitle
,.pagination span strong,.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active
, .pagination span a:hover,.row .pagination span a:hover, li.pagination span a:hover 
,.postlink:hover
{background: #222 none repeat !important;}

.bg1,.bg2,.bg3,.row
{background: #222 !important;}

blockquote
{background: #333 !important;}

input, button, select, textarea{background: #333 none!important;-moz-appearance: none !important;}

iframe {display:none!important;}

a:link, a:visited
{
   color: #ddd !important;
   text-decoration: none !important;
}
   
a:hover{color: #ff6600 !important;}


}
