@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fotka.pl") {

div[id="header"]{display:none !important}
img[src="http://s.asteroid.pl/img/users/brak_k.png"]{display:none !important}
img[src="http://s.asteroid.pl/img/users/brak_m.png"] {display:none !important}
div[id="commentsbox"]{display:none !important}
div[style="width: 340px; float: left; margin-top: 0px;"]{display:none !important}
div[id="menu"] {display:none !important}
div[class="friend_body"] {display:none !important}
div[class="avatar_64_div"]{display:none !important}
div[id*="opis_"]{display:none}
}

@-moz-document url-prefix("http://www.fotka.pl/ranking/najladniejsze_kobiety/") {
div[style="float: left; margin-right: 0.5em; margin-bottom: 1.2em; margin-top: 0.2em; text-align: center; width: 104px; height: 130px; overflow: hidden;"] > a:first-child {display: block !important}
div[style="float: left; margin-right: 0.5em; margin-bottom: 1.2em; margin-top: 0.2em; text-align: center; width: 104px; height: 130px; overflow: hidden;"] > a {display: none}
div[style="float: left; margin-right: 0.5em; margin-bottom: 1.2em; margin-top: 0.2em; text-align: center; width: 104px; height: 130px; overflow: hidden;"] {width: 96px !important; height: 96px !important;}
a[rel="canonical"] {display:none !important}
}

@-moz-document url-prefix("http://www.fotka.pl/szukaj.php") {
div[style="float: left; margin: 10px 0pt;"] {display:none !important}
}
