style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mafiaonline.net") {

	body {
		background-color: #DAD9FF!important;
		color: #000 !important;
	}

	a {
		color: #3166A9 !important;
	}

	a:hover {
		text-decoration: none !important;
	}

	font {
		color: #000 !important;
	}
	li, p, h4 {
		text-align: left;
		width: 50%;
		margin: 0 auto;
	}
	li p, li h4 {
		width: 100%;
	}
}
