style.css:
/*
 Center images (+ dynamic background)
 http://userstyles.org/styles/4150
 BEGIN
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
 url-prefix(http://)
,url-prefix(https://)
{

html > body:first-child
{ background-color: #ddd
; background-image: url(data:image/gif;base64,R0lGODlhCgAKAIAAAAAAAP///yH5BAEAAAAALAAAAAAKAAoAAAIRhB2ZhxoM3GMSykqd1VltzxQAOw==)
}

html > body:first-child > img
{ display: block
; margin: 0px auto
}

html > body:first-child:hover > img
{ background-color: #fff
; outline: 1px solid #000
}

html > body:first-child:hover > img:hover
{ background-color: #000
; outline: 1px solid #fff
}

}

/*
 END
 Center images (+ dynamic background)
 http://userstyles.org/styles/4150
*/
