style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bandzone.cz") {
#userBox { top:85px !important; left:720px !important; width:700px !important; height:auto !important;}
#userBox h4, #userBox h3 {margin-right:5px !important;float:left !important;}
#userBox .clearLeft {clear:none !important;float:left !important; }
#userBox .city,#userBox .age,#userBox .separator {display:none !important;}

#userBox a {float:left !important;}
#userBox li {float:left !important;}
#userBox ul {position:absolute !important;left:40px !important;padding-top:15px !important;}
#userBox div.open {position:relative !important; height:650px !important; float:none !important;}
#userBox li.active {background-color:black !important; width:100px !important; }
#userBox .description {float:right !important;}

.yellow {font-size:12px !important;}

#player {height:auto !important;}
#playerWidget {height:auto !important;overflow:hidden !important;}
#ui-audioplayer-controls {height:55px !important;background-position:0px 0px !important;}
.ui-playlist-scroller {height:60px !important;}
.ui-audioplayer-playlist {height:130px !important;}

.ad {display:none !important;} 
#pageHeaderWrapper {margin-top:0px !important;} 
#contentAd {display:none !important;}

#profileHeader {height:auto !important;}

#profileHeaderWrapper h1 {display:block; width:400px;overflow:hidden;}

#facebookLike {position:relative; top:30px;}
}
