style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("talkingpointsmemo.com") { /* userstyles */


/* odd posts */
li.dsq-comment.dsq-even * blockquote,
li.dsq-comment.dsq-odd {
background-color: #F0EED8  !important;
}
li.dsq-comment.dsq-odd * .dsq-comment-footer-left {
background-color : #ffd8d8 !important;
}
li.dsq-comment.dsq-odd * .dsq-comment-footer-right * .dsq-button-small {
background-color : #c0ffc0 !important;
}

/* even posts */
li.dsq-comment.dsq-odd * blockquote,
li.dsq-comment.dsq-even {
background-color:  /*#F0F0F0*/ #E4F0FF !important;
}
li.dsq-comment.dsq-even * .dsq-comment-footer-left {
background-color : #ffd8d8 !important;
}
li.dsq-comment.dsq-even * .dsq-comment-footer-right * .dsq-button-small {
background-color : #c0ffc0 !important;
}

}
