style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("spiegel.de") 
{

/*Spiegel Online (spiegel.de) ohne Banner, Artikel im Blocksatz und links - 30.12.2010*/


#spTeaserColumn {
 float:left !important;
}
#spArticleColumn {
 float:left !important; width: 450px !important; text-align: justify !important;
}
#spSmallTeaserColumn {
 float:right !important; width: 300px !important;
 margin:0 0 0 30px !important;
}
.spTopicContent #spSmallTeaserColumn {
margin:-20px 0 0 20px !important;
}
.spTopicContent #spColumnWrapperInner {
background:transparent url(http://www.spiegel.de/static/sys/v9/bg/bg_topic.gif) repeat-y scroll 490px 10px !important;
}
#seite2 #spColumnWrapperInner {
background:transparent url(http://www.spiegel.de/static/sys/v9/bg/bg_seite2.gif) repeat-y scroll 532px 10px !important;
overflow:hidden;
}
#seite2 #spSmallTeaserColumn {
margin-left:15px !important;
margin-right:0 !important;
}
/*  Abschnitte entfernt */

#spSocialBookmarkBox, 
#spSocialBookmark,
#spArticleHeadNoLine, 
#spBreadcrumbLine,
#spHistory, 
#spLoginLinks, 
#spInfoBox,
#spPartnerBar,
#spPageFooterMainNav,
#spPageFooterSubNav,
.spAssetAlignLeft, 
.spSpecialArticles, 
.spKopfgrafik,
.spCommunityBox,
.spArticleBottomBox,
.spCommunityBoxHead,
.spArticleNewsfeedBox,
div [class="spCommunityBox spArticleBottomBox spClearfix"]
{
    display: none !important;
}
}
/* Ende */
