style.css:
/* Removes text from toolbarbuttons (optional)*/
#personal-bookmarks .toolbarbutton-text {
	display:none!important
}
/* moves bookmarks toolbar to top*/
/* style for normal window */
#main-window[sizemode="normal"] .chromeclass-directories {
	top: -2px !important;
	height:30px;
}
[sizemode="normal"] .chromeclass-directories {
	position: fixed !important;
	top: 10px !important;
	left: 130px !important;
	background-color: transparent !important;
	border-width: 0 !important;
	margin-right: 115px !important;
}
/* style for maximized window */
#main-window[sizemode="maximized"] .chromeclass-directories {
	top: 3px !important;
	height:30px;
}

[sizemode="maximized"] .chromeclass-directories{
	position: fixed !important;
	top: 5px !important;
	left: 130px !important;
	background-color: transparent !important;
	border-width: 0 !important;
	margin-right: 115px !important;
}
