style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bobandgeorge.com") {

table.table-top > tbody > tr:first-child > td:first-child + td,
table.table-top + table + table > tbody > tr:first-child > td:first-child
{display: none !important}

table.table-top > tbody > tr:first-child td:first-child a,
table.table-top > tbody > tr:first-child td:first-child a img
{display: block; margin: 0 auto}

table.table-top + table td:last-child.border-right
{border: 0 !important}

table.table-top tr:first-child td:first-child,
table.table-top + table tr:first-child td:first-child,
table.table-top + table td:last-child.border-bottom-right
{border-width: 0 0 1px !important}

}
@-moz-document url(http://www.bobandgeorge.com/), url(http://bobandgeorge.com/) {

table.table-top + table > tbody > tr:first-child > td:first-child + td
{display: none !important}

}
