style.css:
@-moz-document domain('brizzly.com') {

/* clean up the background colours */
body { background-color: #f6f6f6 !important; background-image: none !important; }
#main-timeline .header { background-image: none !important; background-color: #CEEFC6 !important}

/* ...and nav colours */
#nav .active { background-image: none !important; background-color: #CFF0FF !important;}
#nav .row:hover { background-color: white !important; color: black !important;  }
#nav .row {border-bottom: 1px solid #eee; }
#nav .un {border-bottom: none !important; color: #479ABF !important; }
#nav .un:hover {background-color: transparent !important; }

/* remove those drop shadows */
#main-timeline, .avatar, .tab .label, .fixie, #app-name, .timeline-profile-visible, .cameo { -webkit-box-shadow: none !important; box-shadow: none !important; -moz-box-shadow: none !important; }

/* get rid of those drop shadows and glows around new tweets */
.timeline-profile-visible { background-color: #CFF0FF !important; border-color: #CFF0FF !important; }
.fixie{ background-color: #eee !important; }
#app-name { background-color: #eee !important; }
.new-item .avatar { border-color: #FEFF6F !important; }

}
