/* Fix styling for close button size and position */
.tab-close-button {
   -moz-image-region: rect(0 56px 14px 42px) !important;
}

/* Change close button to red on current tab only for non-protected tabs (Tab Mix Plus) */
tab:not([protected="true"]) .tab-close-button[selected="true"] {
   -moz-image-region: rect(0 14px 14px 0) !important;
}
/* Change close button to X on hover only for non-protected tabs (Tab Mix Plus) */
tab:not([protected="true"]) .tab-close-button:hover {
   -moz-image-region: rect(0 28px 14px 14px) !important;
}
/* Change close button to pressed state only for non-protected tabs (Tab Mix Plus) */
tab:not([protected="true"]) .tab-close-button:hover:active {
   -moz-image-region: rect(0 42px 14px 28px) !important;
}
