style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.omgubuntu.co.uk") {

#header {
margin: 35px 0px -13px 0px !important;
}

.post-inner {
width: auto !important;
float: none !important;
}

div.left-block {
display: none !important;
}

blockquote {
display:none;
}

li#scrolly-banner-thing {
display: none !important; 
}

#most-read-posts a {
font-size: 14px !important; 
}

div#content {
font-size: 13px !important;
line-height: 1.5 !important; 
font-family: Verdana,"Ubuntu","UbuntuBeta","Arial",sans-serif !important;
margin-top: 15px !important;
padding-top: 10px !important;
}

li.dsq-widget-item {
font-family: Verdana,"Ubuntu","UbuntuBeta","Arial",sans-serif !important;
}

#menu-primary-menu a {
text-transform: none !important;
font-size: 11px !important;
}

div.entry-share {
width: 560px !important;
}

li#recent-disqus-comments li.dsq-widget-item {
width: auto !important;
margin: 15px 0 !important;
}

img.dsq-widget-avatar {
width: 40px !important;
height: 40px !important;
margin-right: 5px !important;
}

}

@-moz-document url("http://www.omgubuntu.co.uk/") {

.post-inner {
width: 500px !important;
}

div.left-block {
display: block !important;
}

}
