style.css:
@-moz-document domain("twitter.com") {
.tweet-pane .tweet-text, .main-content .tweet-content .tweet-row .tweet-text, .linked-text, #status {
	/*[[fontsize]]*/
	/*[[dirchoose]]*/
	color: #636363 !important;
	padding-bottom: 5px !important;
}
.tweet-text-large, .in-reply-to .tweet-text, .tweet-pane .tweet-text, .recent-tweets .tweet-text, .related-tweets .tweet-text, .details-pane {
	font-size: 12px !important;
	font-family: tahoma !important;
	/*[[dirchoose]]*/
	color: #636363 !important;
	padding-bottom: 5px !important;
}
.recent-list-item, .latest-favorite .tweet-text, .dashboard .tweet-content .tweet-row .tweet-text {
	font-family: Tahoma !important;
	font-size: 10px !important;
	/*[[dirchoose]]*/
	color: #636363 !important;
}

textarea.twitter-anywhere-tweet-box-editor {
	font-family: Tahoma !important;
	/*[[dirchoose]]*/
}

span.user-full-name {
	font-family: Tahoma !important;
	font-size: 10px !important;
}

/*[[avatarfloat]]*/

.component .tweet-text{
	font-family:tahoma !important;
}

}
