style.css:
@-moz-document domain('codereddit.com') {

	body
	{
	    font-family:Courier New;
	    background-color:black;
	    color:#DDD;
	}
	a
	{
	    color:#EEE!important;
	    text-decoration:none;
	}
	a:hover
	{
	    text-decoration:underline;
	}

	.comment
	{
	    color:#3A8000;

	}

	.keyword
	{
	    color:#999!important;
	}

	.class
	{
	    color:#2B91AF;
	}

	.string
	{
	    color:#A31545;
	}

	.indent
	{
	    padding-left:40px;
	}

}
