style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {


/* removing ugly adds */

div.fbEmuEgoUnitFirst {
  display: none;
}

#sidebar_ads {
  display: none;
}

div.fbEmuEgoUnit {
  display: none;
}

/* removing friendlist */

.profile-friends {
  display: none;
}

/* making content area longer */

#contentArea {
  width: 760px !important;
}

.uiUfi {
  width: 100% !important;
}

/* removing right sidebar */

#rightCol {
  display: none;
}

/* removing top photo bar */

#pagelet_photo_bar {
  display: none;
}

/* removing right online bar */

.fbChatSidebar{
   display: none !important;
}

}
