@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(youtube.com) {
    /* Clean up page */
    #pagetop, #footer-container, #homepage-side-content, #watch-panel, .first,
    #watch-branded-actions, #branded-playlist-module, #ppv-container {
        display: none !important;
    }

    /* Unset VEVO background images and colours */
    body, #content { background: white !important; }
    #watch-headline-title { color: black !important; }
    #watch-username { color: blue !important; }
}
