@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("csgalaxies.com") {

/* OPTIONAL SETTING:
 * let's get rid of the main map grid! :)
 * comment this section to restore the map grid.
 * commenting is done simply by putting a
 * whitespace between * and / in the next line!
 */
script[src*="locations"]~table[cellspacing="1"]{
  border-collapse: collapse!important;
}

/* the links */
a,
a:visited{
  color:#B3DF00!important;
  -moz-border-radius: 5px;
  margin:1px!important;
  padding: 0px 2px;
}
a[href*="user_info"]{
  color:#7DF0FF!important;
}
a[href*="faction_info"]{
  color:#AB82FF!important;
}
/* special login button */
input.forms_btn[name="login"]{
  background-color:#010101 !important;
  color:#C0C0C0 !important;
  -moz-box-shadow: 0 0 5px 2px #7DF0FF;
}
/* text input fields get a new style and slightly rounded corners */
input.forms,
input.forms_txt,
textarea.forms{
  background-color:#222 !important;
  color:#ccc !important;
  -moz-border-radius: 3px;
  padding:2px;
  height:auto !important;
}
/* amount fields */
input[name="amount"]{
  padding:0px 1px;
  text-align:right!important;
  height:15px !important;
  -moz-border-radius: 3px;
}
/* planet thing fields */
input[value="Sell"],
input[value="Buy"],
input[value="Accept"]{
  margin:2px 0px 2px 2px !important;
  padding:0px 0px 1px 0px;
  height:15px!important;
}
input[value="Hire"],
form[action*="bounty"] + td>input[value="Accept"]{
  margin:0px !important;
  padding:0px!important;
  height:auto!important;
}
/* file upload (symbols) fields */
input[type="file"]{
  height:auto!important;
}
/* ship info */
input.forms_txt_fade{
  -moz-border-radius: 3px;
  margin:1px;
}
/* ship info fields get blue shadow on hovering */
input.forms_txt_fade:hover{
  -moz-box-shadow: 0 0 5px 2px #39f;
}
/* active buttons, tables get rounded corners */
input.forms_btn,
table{
  -moz-border-radius: 5px;
}
/* drop downs get rounded corners */
select.forms{
  padding:1px;
  -moz-border-radius: 5px;
}
/* deactivated buttons and those who bring you just to the sector view
 * get rounded corners & the same style
 */
input.forms_btn_off,
input.forms_btn_view{
  border: 1px solid #404040 !important;
  color: #404040 !important;
  -moz-border-radius: 5px;
}
/* make the price search button stand out */
input[value="E"],
input[value="S"]{
  -moz-border-radius: 5px;
  color:#C0C0C0!important;
  padding: 0px 2px 1px 3px;
  width:22px!important;
  text-align:center!important;
}
/* new navigation computer spacing */
input[style="width: 22px;"]{
  margin:1px!important;
}
input[style="width: 96px;"]{
  margin:1px!important;
  width:102px!important;
}
/* give the the links a different colour when hovering */
a:active,
a:hover{
  color:#7DF0FF!important;
  border: 1px solid #9D9DA1;
  margin:0px!important;
}
/* give the the buttons a different colour & shadow when hovering */
input.forms_btn:active,
input.forms_btn:hover{
  color:#7DF0FF!important;
  -moz-box-shadow: 0 0 5px 2px #7DF0FF;
}
/* give deactivated buttons on hovering a red shadow & font colour */
input.forms_btn_off:hover,
input.forms_btn_view:hover{
  color:#f33!important;
  -moz-box-shadow: 0 0 5px 2px #f33 ;
}
/* remove hover effect for 16x16 image links
 * when found inside a properly formatted cell
 */
td[width="16"]>a[href*="user_info"]:active,
td[width="16"]>a[href*="user_info"]:hover{
  color:transparent !important;
  background-color:transparent!important;
  border: none!important;
  margin:1px!important;
}
/* remove all borders from inside the main table */
td[style*="border-style"]{
  border-style:none!important;
}
/* different background on active grid section */
img[src*="selected"]{
  background-color:#ff0;
  opacity:0.1;
}
/* the current position. placeholder for future reference */
img[src*="current"]{
}
/* different background on grid hover */
img[src*="over"]{
  background-color:#f00;
  opacity:0.2;
}
/* create exception for map links */
div[style*="position: relative;"]>a:active,
div[style*="position: relative;"]>a:hover{
  color:transparent !important;
  background-color:transparent !important;
  border: none !important;
  margin:0px!important;
}

}
