style.css:
@namespace url(http://www.w3.org/1999/xhtml);

/* ASCII.jp */
@-moz-document domain("ascii.jp") {

  #mainC p, #mainC dd {
    font-size: 100% !important;
    color: #222222 !important;
  }
  h1, #mainC h2 {
    text-shadow: 0 1px 0 #FFFFFF;
  }
  body {
    background: #EEEEEE !important;
  }
  #container,#mainC {
    float: none !important;
    margin: 0 auto !important;
  }
  #jpWrap { width: inherit !important; }

   #header
  ,#sideR
  ,#sideL
  ,.pickwrap
  ,#artAds
  ,#footer
  { display:none !important; }

}

/* PC Watch */
@-moz-document domain("pc.watch.impress.co.jp") {

  .titleHeader
 ,.ipw_left
 ,.ipw_right
 ,.ipw_main>br:first-child+div
 ,.social_bookmark
 ,hr
 ,.ad_contents_under
 ,.ad_contents_footer_copyright
 { display: none !important; }

  div.contents {
    width: 800px !important;
    margin: 0 auto !important;
  }

}

/* ITpro */
@-moz-document domain("itpro.nikkeibp.co.jp") {

  body {
    border: none !important;
    background-color: #F5F5F5 !important;
    text-shadow: 0 1px 0 #FFFFFF;
  }
  #mainTable { background: none !important; }
  #kiji { font-size: 90% !important; color: #333333; }

   #header
  ,div[class^="gnav-"]
  ,#sideNavi
  ,#subContent
  ,.leafThemeTitle
  ,.themeTopic
  ,.bottomButton
  ,#myitproNote
  ,#relationKeyword
  ,#bottomPickup09
  ,#siteInformation
  ,#footer
  {display:none !important;}

}
