style.css:
@-moz-document url(chrome://browser/content/browser.xul){
	/* Global */
	#urlbar {
		-moz-border-radius: 3px 3px 3px 3px !important;
	}
	
	/* Large Icons */
	[iconsize="large"] #nav-bar > .toolbarbutton-1:not(#back-button), toolbar #forward-button {
		box-shadow: none !important;
	}

	[iconsize="large"] #nav-bar > .toolbarbutton-1:not(#back-button), toolbar #forward-button {
		-moz-border-radius: 3px 3px 3px 3px !important;
	}

	[iconsize="large"] #nav-bar #back-button {
		box-shadow: 1px 1px 1px 1px rgba(255,255,255,.3) inset,
				  1px 1px 1px 1px rgba(255,255,255,.1) inset,
				  1px 1px 1px 1px rgba(0,0,0,.15),
				  1px 1px 1px rgba(0,0,0,.4),
				  1px 1px 1px rgba(0,0,0,.3),
				  1px 1px 1px rgba(0,0,0,.2) !important;
	}


	/* Small Icons */
	#navigator-toolbox[iconsize="small"] #back-button, #nav-bar > .toolbarbutton-1, #forward-button  {
		box-shadow: none !important;
	}

	#navigator-toolbox[iconsize="small"] #nav-bar > .toolbarbutton-1 {
		-moz-border-radius: 3px 3px 3px 3px !important;
	}

	#navigator-toolbox[iconsize="small"] toolbar #back-button {
		-moz-border-radius: 3px 0 0 3px !important;
	}

	#navigator-toolbox[iconsize="small"] toolbar #forward-button {
		-moz-border-radius: 0 3px 3px 0 !important;
	}
}
