style.css:
@-moz-document url-prefix("http://anpi.tv/") {

/* サイドバー(右側)を非表示にして入力欄を広く*/
.span-18 {
  width: auto ! important;
}

/* 入力フォームのメモ欄(いちばん右側のカラム)を非表示に */
form table td:nth-child(3) {
  display: none ! important;
}

/* サイドバーをサイドに出さない*/
div#side {
  float: none !important;
}

/* サイドバーの中のメニュー以外を非表示に */
div#side iframe {
  display: none !important;
}
div#side p br {
   display: none ! important;
}

div#side > h3:nth-child(5) {
  display:none;
}
div#side > h3:nth-child(8) {
   display: none ! important;
}
div#side p:nth-child(9) {
  display:none ! important;
}
div#side p:nth-child(10) {
  display:none ! important;
}

/* サイドバーのメニューをトップに */
div#side > p:nth-child(6) {
  position: fixed ! important;
  top: 0;
  background-color: white !important;
  border: 1px solid gray !important; 
}

/* 検索ボックスを非表示に */
div#search {
  display: none;
}

}
