style.css:
@-moz-document domain('www.sinfest.net'), url-prefix('http://www.sinfest.net') {
div[style*=position] > table { display: none; }

div[style*=position] div[style*=position] { display: none; }

center center { display: none; }

p[align=center] + p[align=center] { display: none; }

table { padding-top: -2em; }

blockquote { display: none; }
}
