style.css:
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document
	domain(ize.hu){
		.banner,
		#habostorta,
		IFRAME[height="55"][width="980"],
		#footer,

		#sidebar-right > P,
		#onlinestat,
		#content2 > H3,
		#izehirlevelform2,

		DIV[style="border: none; padding: 0px; text-align: center;"],
		SPAN[style="display: block; text-align: center; margin-top: 10px;"],
		.shareLine,

		#sidebar-left > H2:first-child,
		#sidebar-left > H2 + * + H2 + * + H2,
		#sidebar-left > IFRAME[style="border:none; overflow:hidden; width:203px; height:385px; margin-top: 10px; background: white;"],

		#radiobox,
		#toplistbox,
		.hirizebox,

		DIV[style="margin-top: 20px; text-align: center;"],
		.goAdverticum
	{display:none !important;}

	.izeButtons {margin-top:90px !important;}
}


 @-moz-document
	domain(hir.ize.hu){ .izeButtons {margin-top:0px !important;} #online, .userList, :first-child + * + * + * + .leftBox, :first-child + * + * + .leftBox, #bottom {display:none !important;} }


/* @-moz-document
	domain(art.ize.hu), domain(auto.ize.hu), domain(film.ize.hu), domain(pink.ize.hu) {.leftBox + * + * + .leftBox {display:none !important;} }
*/

 @-moz-document
	domain(game.ize.hu) {#sidebar-left > .leftBox {display:none !important;} }


 @-moz-document
	domain(sport.ize.hu) {:first-child + * + * + * + H2 {display:none !important;} }


 @-moz-document
	domain(tech.ize.hu) {.leftBox{display:none !important;} }




 @-moz-document
	domain(funpic.hu) {
	 #containerPic {width:1000px !important; margin-left:auto!important; margin-right:auto!important;}
	 #pictureData .column1 {max-width:560px!important;}
	 #picturePrevNext, #similarPictures  {margin-top:30px !important;}
	 #picturePrevNext > .prev {padding-left:40px!important;}
	 #picturePrevNext > .next {padding-right:25px!important;}
	 #similarPictures > LABEL {text-align:center !important;}
	 #similarPictures > .gallery > LI > a { background: url("http://i.imgur.com/YmOk4.png") no-repeat scroll 0 0 transparent !important;}
	 #similarPictures > .gallery > LI > a:hover { background-position: 0 -129px !important;}

		[id*="Banner"],
		[class*="Banner"],
		IFRAME[src^="http://www.facebook.com"],
		#pictureShare,
		.tip,
		.goAdverticum,
		#copyright,
		#wibiyaToolbar,
		DIV[style="height: 40px; clear: both;"]
		/*.thumbexp_ignore*/
	 {display:none !important;}
	}




 @-moz-document
	domain(funvid.hu) {
		DIV[style="padding: 3px 0px 0px 26px; height: 90px; clear: both;"],
		.column-video-left > .cell.marginTop10.padded5.centerText,
		.video + * + * + [class^="cell marginTop"],
		.goAdverticum,
		.videoResponses,
		.footer,
		#wibiyaToolbar
	 {display:none !important;}
	}




 @-moz-document
	domain(topclub.hu) {
	Body {background:#141414 !important; background-image:none !important;}
	embed,[id^="banner"],.goAdverticum,[class^="banner"],#divFlashTeaser,#right,:first-child + * + * + * + .frame1,DIV[style="width: 1500px; height: 60px; clear: both; position: fixed; z-index: 2000; bottom: 0px; left: 0px;"],SPAN[style="white-space:nowrap;"] {display:none !important;}
	.galleryPage, .post {width:100% !important;}
	.datasheet{width:736px !important;}
	.message {width:745px !important;}
	.pic {margin-left:100px !important;}
	.gallery {margin-left:22px !important;margin-right:auto !important;}
	.more {margin: 50px 90px 0 90px !important;}
	}
