style.css:
/*************************** Cleanup Tabs ****************************/
.tabbrowser-tab { -moz-appearance: none !important;
		  min-width: 150px !important;
		  background-color: transparent !important;
		  background-image: none !important;
		  border: none !important;
		  height: 30px !important;
		  max-height: 30px !important;
		  margin-top:-2px !important;
		  margin-bottom:-3px !important;
		  padding: 0 !important; }

.tabbrowser-tab .scrollbox-innerbox { -moz-box-pack: center !important; }
.tabbrowser-tab .tab-icon { display: none !important; }


/***************************** Destroy, Attack, Die **********************/
toolbarbutton, statuspanel, .tabs-alltabs-button, .tabs-alltabs-box, .tabs-alltabs-box-animate, #identity-box:not([style="-moz-user: ignore;"]), .tabbrowser-arrowscrollbox + stack, .autocomplete-history-dropmarker, .urlbar-icon, .tab-icon-image, .tabs-newtab-button, .tab-close-button, #openLocation > hbox > vbox > hbox:last-child, #openLocation > hbox > vbox > separator, #openLocation > hbox > vbox > description, #openLocation > hbox > vbox > description:first-child + separator + hbox > button, #openLocation > .dialog-button-box {display: none !important;}

/*************************** I Forgot What It Does ****************************/
.tabbrowser-tab[selected="true"]{ width: 150px !important; text-align: center !important;  max-height: 23px !important;padding-top: 0px !important; margin: 2px 5px 0px 0px !important; height: 30px !important;-moz-appearance: none !important; border-top: 0px !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important; font-weight: bold !important;}
.tabbrowser-tab[selected="true"]:hover{ text-align: center !important;  max-height: 23px !important;padding-top: 0px !important; margin: 2px 5px 0px 0px !important; height: 30px !important;-moz-appearance: none !important; border-top: 0px !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important; font-weight: bold !important;}
.tabbrowser-tab[selected="false"]{width: 150px !important; text-align: center !important;  max-height: 23px !important;padding-top: 0px !important; margin: 2px 5px 0px 0px !important; height: 30px !important;-moz-appearance: none !important; border-top: 0px !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important;}
.tabbrowser-tab:not([selected="true"]):hover {text-align: center !important;  max-height: 23px !important;padding-top: 0px !important; margin: 2px 5px 0px 0px !important; height: 30px !important;-moz-appearance: none !important; border-top: 0px !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important; font-weight: bold !important;}
.tabbrowser-tab:not([selected="true"]){ text-align: center !important;  max-height: 23px !important;padding-top: 0px !important; margin: 2px 5px 0px 0px !important; height: 30px !important;-moz-appearance: none !important; border-top: 0px !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important;}

#content spacer,.tabs-bottom {display: none !important}
#content tab:not([selected="true"]) label {opacity: 0.95 !important;} 
#content tab:not([selected="true"]) {opacity: 1 !important;}

/*************************** White Flash Fix ****************************/
tabbrowser tabpanels { background: none !important; }
