style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mybristol.ru"), url-prefix('http://mybristol.ru') {
  body {padding:5px !important;}
  td.header {height:0px !important;}
  td.header img {height:0px !important; display:none !important;}
  td.windowbg {background: #EFFDF3 !important}
  table.bordercolor table table tr:last-child table tr:last-child {
    display:none;
  }
  table.bordercolor table table td:first-child div.smalltext{
    height: 65px; 
    overflow: auto;
    position: relative;
    color: transparent;
  }  
  table.bordercolor table table td:first-child div.smalltext img {
    display: none;
  }
  table.bordercolor table table td:first-child div.smalltext a { 
    color: transparent !important;    
  }  
  table.bordercolor table table td:first-child div.smalltext a img {
    display: block;    
  }
  table.bordercolor table table td:first-child div.smalltext img.avatar {
    display:block;
    position: absolute;
    top: 1px;
  }
}
