style.css:
/* Faciconized Tabs Non Selected*/
tab[faviconized="true"] {
   min-width:32px !important;
   max-width:32px !important;
} 
tab[faviconized="true"] .tab-icon-image {
   margin-left: 5px !important;

}

/* Selected Faviconized Tabs */
tab[selected="true"][faviconized="true"]{
   min-width:32px !important;
   max-width:32px !important;
}
tab[selected="true"][faviconized="true"] .tab-icon-image {
   margin-left: 5px !important;
}
