@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("launchy.net") {

/* REMOVE ADS */
div[id*=ads], #bannerad { display: none !important; }

/* USE AD SPACE FOR THE CONTENT */
#content { width: 100% !important }

}
