style.css:
/***
GoogleCalendar - SmpRip (v0.770) 03:46 12-01-2012
by: Newtomic
http://userstyles.org/styles/46588
***/
@-moz-document
url-prefix("http://www.google.com/calendar/"),
url-prefix("https://www.google.com/calendar/"),
url-prefix("http://www.google.pt/calendar/"),
url-prefix("https://www.google.pt/calendar/") {
/* ALL buttons > less margins */
.goog-imageless-button-content {
	margin: 0 2px !important;
}
/* left sidebar-nav > new smaller size */
TD#nav.noprint {
	width: 150px !important;
}
/* left sidebar-top-nav > padding tunning */
.qnb-container {
	padding: 2px 0px 2px 2px !important;
}
/* left sidebar-nav > small callendar > remove excess white */
.dpdiv {
	padding: 0 2px !important;
}
/* left sidebar-nav > small callendar > month title > remove excess white */
TD.dp-cell.dp-sb-cur {
	padding: 0 0 0 2px !important;
}
/* left sidebar-nav > small callendar > prev/next buttons > padding + margin = more clean */
TD.dp-cell.dp-sb-nav {
	margin: 0 !important;
	padding: 1px 4px 0 0 !important;
}
/* logo > new_position + new_size */
IMG#mainlogo {
	position: fixed !important;
	left: 5px !important;
	bottom: 0 !important;
	width: 140px !important;
}
/* horizontal Gcal main nav > whole block > reposition*/
TD#mainnav {
	position: fixed !important;
	left: 150px !important;
	width: 100% !important;
}
/* horizontal Gcal main nav > left zone */
TABLE.nav-table {
	position: relative !important;
	top: -2px !important;
	font-size: 89% !important;
}
/* horizontal Gcal main nav > left zone > today button */
#todayButton\:0 {
	margin-left: 1px !important;
	font-size: 80% !important;
}
/* horizontal Gcal main nav > left zone > back/forward buttons   |AND|   left sidebar-nav > small callendar > prev/next buttons   |> main definitions */
.navbutton.navBack.goog-inline-block,
.navbutton.navForward.goog-inline-block,
SPAN.dp-cell.dp-sb-prev.goog-inline-block,
SPAN.dp-cell.dp-sb-next.goog-inline-block {
	background: #36C no-repeat center !important;
	border-radius: 2px !important; /*CSS3*/
	-moz-border-radius: 2px !important; /*Mozilla*/
	-webkit-border-radius: 2px !important; /*WebKit*/
	width: 11px !important;
	height: 11px !important;
	padding: 0 !important;
}
/* horizontal Gcal main nav > left zone > back button   |AND|   left sidebar-nav > small callendar > prev button   |> img background + margins */
.navbutton.navBack.goog-inline-block,
SPAN.dp-cell.dp-sb-prev.goog-inline-block {
	margin: 0 2px 0 3px !important;
	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAALAQAAAACb66oFAAAAAnRSTlMAAHaTzTgAAAAZSURBVHjaY2BgYGHiYBJgUmByYP4HgQwMAB7WBIkwHMmFAAAAAElFTkSuQmCC) !important;
}
/* horizontal Gcal main nav > left zone > forward button   |AND|   left sidebar-nav > small callendar > next button   |> img background + margins */
.navbutton.navForward.goog-inline-block, SPAN.dp-cell.dp-sb-next.goog-inline-block {
	margin: 0 -5px 0 0 !important;
	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAALAQAAAACb66oFAAAAAnRSTlMAAHaTzTgAAAAbSURBVGjeY2BgcGBIYChgqGCoAeICINuBgQEAJxoDjZsH6BAAAAAASUVORK5CYII=) !important;
}
/* horizontal Gcal main nav > left zone > date > bigger font size */
.date-top {
	font-size: 139% !important;
}
/* horizontal Gcal main nav > right zone */
#topRightNavigation {
	position: relative !important;
	right: 158px !important;
	font-size: 85% !important;
	word-spacing: -1px !important;
	letter-spacing: -0.25px !important;
}
/* horizontal Gcal main nav > right zone > refresh textlink > margin tweaks */
A.mg-refresh {
	margin-left: -10px !important;
	margin-right: 3px !important;
}
/* horizontal Gcal main nav > right zone > print icon > rip */
/*DIV.mg-print-img.goog-inline-block {
	width: 0 !important;
}*/
/* notification msgs > new position and tweaks */
#nt2 {
	padding: 0 4px 0 3px !important;
	font-size: 85% !important;
	word-spacing: -0.5px !important;
	letter-spacing: -0.125px !important;
	position: fixed !important;
	top: 2px !important;
	left: 355px !important;
	border-radius: 5px !important; /*CSS3*/
	-moz-border-radius: 5px !important; /*Mozilla*/
	-webkit-border-radius: 5px !important; /*WebKit*/
	z-index: 1000 !important;
}
/* Logo and Search Zone > RIP */
#topBar .noprint {
	height: 0 !important;
}
/* Search Things > whole block */
FORM.msf-container.goog-inline-block {
	z-index: 1 !important;
	font-size: 75% !important;
	word-spacing: -2px !important;
	letter-spacing: -0.25px !important;
}
/* Search Things > text-box only */
INPUT#maininput {
	margin-right: 2px !important;
	position: fixed !important;
	top: 20px !important;
	right: 336px !important;
	font-size: 110% !important;
	width: 50px !important;
	height: 14px !important;
}
INPUT#maininput:focus {
	width: 333px !important;
}
/* Search Things > button */
.goog-inline-block.goog-imageless-button.msf-button.goog-imageless-button {
	padding: 0 !important;
	position: fixed !important;
	top: 16px !important;
	right: 240px !important;
	word-spacing: -1px !important;
	letter-spacing: -0.5px !important;
}
/* Search Things > hiperlink 'Show search options' */
SPAN#sroptlink.lk.small {
	position: fixed !important;
	top: 31px !important;
	right: 245px !important;
	font-size: 90% !important;
}
/* Event Details > More Actions Drop Box > smaller horizontal size */
SELECT.prefselect.ep-ea-ma {
	width: 100px !important;
}
/* Event Details > bottom links > re position */
A.ep-ats-cl {
	position: absolute !important;
	top: 170px !important;
	right: 15px !important;
}
/* Big Calendar Zone > Container > margin tweaks */
.mv-container {
	margin: 0 -7px 0 -2px !important;
	padding: 0 !important;
}
}
