style.css:
@-moz-document domain("heise.de") {
body
  {
    background: #FFF !important;
  }  
   
  div#container
  {
    position: relative !important;
    width: auto !important;
   }
  
  div#mitte_links, #mitte_links
  {
    position: relative !important;
    width: auto !important;
   }
  
  .article-layout__content.article-content {
    position: relative !important;
    width: auto !important;
  }
  
  .a-layout__main {
    position: relative !important;
    width: auto !important;
    }
  
div#bannerzone, div#bannerzone_top, div#mitte_rechts, #mitte_rechts, div.cadv, div.cadv_start, div.bcadv, div.bcadv_start, div.adbottom, .banner, .sky, .skyscraper, .heise_advert, table.druck, .leaderboard, .ISI_IGNORE table, div.teaser_liste_foren, div.teaser_liste_software, div.naviad, div.teaser_adliste, div.clear, div.online-markt, div.col100preisvergleich, div.col100weiss, div.col50anschnitt, div.teaser_abdeckung, section.sidebar, section.article-sidebar, div.a-layout__sidebar
  {
    display: none !important;
  }
}

@-moz-document url-prefix("https://www.heise.de/tp/"), url-prefix("http://www.heise.de/tp/") {
html, body.tp, div.container_all, div.row, div.col-md-10, div.container, div.row.tp_content, main.col-md-8, article.tp_topteaser, div#zeile_b.topteaser_master.layout_6.print-avoid-pagebreak-inside, div.multiple
  {
    background-color: #FFFFFF !important;
    width: auto !important;
    font-size: 1em;
  }

  /*werbung weg*/
  div#zeile_b, .grossbild, .teaser_frei
  {
    display: none !important;
  }
 
  /*bilder kleiner*/ 
  .a-size-defined
  {
    width: 200px;
    /* you can use % */
    height: auto;
  }

body#pre-akwa.tp div.container_all div.row div.col-md-10 div.container div.row.tp_content main.col-md-8 section.topteaser-container div#zeile_a.topteaser_master.layout_5.print-avoid-pagebreak-inside a.the_content_url div.img_clip
  {
    width: 200px;
    /* you can use % */
    height: auto;
  }
            
  /*teaser*/
  .the_content_url
  {
    position: relative;
    top: auto;
    margin-left: 1px;
    width: 1000;
    height: auto;
  }

                        
  /*nachrichten*/

  .col-md-4
  {
    position: relative; 
    top: auto;
    margin-left: 1px;
    width: 180px;                    
  }

  .col-md-8
  {
    position: relative;
    top: auto;
    margin-left: -1px;
  }

  .col-xs-12
  {
    position: relative;
    top: auto;
    margin-left: -1px;
    width: 200;
    height: 150px;
  }

  .beitragsfooter
  {
    position: relative;
    top: 100px;
    margin-left: -1px;
    width: 200;
    height: 150px;
  }
}
