style.css:
/* 
Live-Ticker auf tagesschau.de ausblenden

Von: http://userstyles.org/styles/46903

Letztes Update: Januar 2015
*/

@-moz-document domain("tagesschau.de") {
.modTicker {background-color:#DC143C !important;padding:10% 2% !important;border-bottom:none !important;color:#FFFAFA !important}
.modTicker > * {display:none !important}
.modTicker:before {content:"Live-Ticker ausgeblendet!";font-size:xx-large !important}
.modTicker:after {content:"Vorsicht beim Runterscrollen!";font-size:xx-large !important}
}
