style.css:
@-moz-document domain("nya.sh") {

.sh { width: 94% !important; }
#body {
   width: 100% !important;
   box-shadow: #aaa 0 0 4px;
}

.sh_t div, .sh_b div, .sh_l div, .sh_r div,
.sh_t, .sh_b, .sh_l, .sh_r { background-image: none !important; }

#inner-body {
   padding-left:  8px;
   padding-right: 8px;
}

#inner-body div[style^="float: left;"]{
   width: -moz-calc(100% - 203px) !important;
   width: -webkit-calc(100% - 203px) !important;
   width: calc(100% - 203px) !important;
}
.q {
   padding-left:   0.5em !important;
   padding-right:  0.5em !important;
   padding-bottom: 1em !important;
   background-color: #f6f6f6;
}

.sms-header {
   border-bottom: 1px solid #ccc;
   text-shadow: #fff 1px -1px;
}

.toplink { text-shadow: #5a3f99 1px -1px 1px; }

}
