style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {


/* The Like Button Size */

a.connect_widget_like_button, a.connect_widget_unlike_link {
height: 45px ! important;
width: 100px ! important;
border: 0px ! important; 
}

/* The Like Button Image */

.like_button_no_like {
background: url(http://img854.imageshack.us/img854/217/clickz.png) no-repeat left ! important;
}


/* The Unlike Button Image */

.like_button_like, a.connect_widget_unlike_link {
background: url(http://img69.imageshack.us/img69/1535/clicked.png) no-repeat left ! important;
}



/* Don't like */
div.like_box .UIImageBlock_Content:after { content:" nicht" }


/* Remove the like */
button.like_link:after { content:" nicht" }


}
