@namespace url(http://www.w3.org/1999/xhtml);

/* 
 Gmail: Hide Report as Spam, Delete & Refresh buttons for horizontal Multiple Inboxes

 This is especially useful when Multiple Inboxes are enabled at the right side.
 Disable user style for doing the occasional 'Report as Spam' (e.g. through the Stylish icon in the bottom-right corner).

 By SanderK. Last updated: 29 May 2011.
*/
@-moz-document url-prefix("http://mail.google.com/mail/"),
               url-prefix("https://mail.google.com/mail/") {

/* Hide Report as Spam, Delete & Refresh buttons: */
DIV[act="9"], DIV[act="10"], DIV[act="20"]{
 display: none !important;
}

}
