style.css:
@-moz-document domain(forums.white-wolf.com)
{
	
	/*Changing width to use whole page*/
	#container {
		width: 95% !important;
	}
	
	#navigation {
		width: 100% !important;
		background-size: 100% !important; 
	}
	
	footer {
		width: 100% !important;
		background-size: 99% !important; 
	}
	
		
	#search{
		float: right !important;
		left: 0px !important;
	}
	
	td.postUser {
		width: 200px !important;
	}	

	td.postPosted  {
		width: 90% !important;		
	}
	
	td.header2{
		text-align: left !important;
	}
	
	/* Pink Selections bad, fixing */
	::-moz-selection{ 
		background: #0000CD	 !important;
		
	} 	
	
	::selection{ 
		background: #0000CD	 !important;
		

	} 	
 
}
