style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bib.umontreal.ca") {

body {
    background: url("http://www.umontreal.ca/enseigner/images/iu/fond-bodypng.png") repeat-x scroll 0 89px transparent !important;
}

div#um-bandeau-institutionnel {
    height: 89px !important;
}

div#um-bandeau-institutionnel a.um-logo {
    background: url("http://www.umontreal.ca/enseigner/images/iu/logo.gif") no-repeat scroll 0 0 transparent !important;
    width: 196px;
    height: 61px;
}

div#um-bandeau-institutionnel a.um-logo img {
    display: none !important;
}

div#um-bandeau-institutionnel a.um-logo:after {
    content: "";
    display: block;
    border-left: 2px solid #666666;
    height: 30px;
    left: 194px;
    position: absolute;
    top: 30px;
}

/* Bandeau Bib */

div#um-bandeau-identification {
    background-image: none !important;
    height: 32px !important;
}

div#um-bandeau-identification ul#um-menu-drop-down {
    top: 0 !important;
}

/* Logo Bib */

div#um-bandeau-identification div.um-bandeau-site-complet a {
    display: block;
    height: 60px;
    width: 290px;
    position: absolute !important;
    top: -50px;
    left: 204px;
    background: url("http://www.bib.umontreal.ca/images/modele/test-logo-bib.print.jpg") no-repeat scroll 0 0 transparent !important;
}

div#um-bandeau-identification div.um-bandeau-site-complet img {
    display: none !important;
/*    position: absolute !important;
    top: -50px;
    left: 204px; */
}

/* Pied de page */

div#um-pied {display: none;}
div#um-enveloppe:after {
    background: url("http://www.bib.umontreal.ca/images/modele/test-pied-page-UdeM.png") no-repeat scroll center 0 #8e8e8e !important;
    border: 0 !important;
    height: 284px;
    width: 100%;
    position: absolute;
    overflow: hidden;
    content: "";
    display: block;
}

div#um-menu-et-contenu {
    margin-top: 20px;
}

div#um-contenu {
    padding-bottom: 0 !important;
}

div#um-pied * {
    display: none;
}


}
