style.css:
@-moz-document url-prefix('http://www.ambrosiasw.com/forums') {

    iframe, #footer_utilities, #board_footer, .moderation_bar, #content > br, .topic_share, .topic_jump {
      display: none;
    }
    .post_wrap h3 {
      float: left;
      width: 200px;
      border: 0;
      margin: 4px;
      padding: 0;
      background: transparent;
    }
    .post_wrap .author_info {
      clear: left;
      width: 200px;
      margin: 4px;
      border-right: 0;
    }
    .post_wrap .author_info > * {
      display: none;
    }
    .post_wrap .author_info .user_details {
      display: block;
      border: 0;
    }
    .post_wrap .post_body {
      margin-left: 210px;
      margin-bottom: 0 !important;
    }
    .post_wrap .rep_bar {
      display: none;
    }
    .post_wrap .post_controls {
      padding: 4px;
    }
    .post_wrap .post_controls a, .post_wrap .post_controls li {
      background: transparent !important;
      display: inline;
      float: none;
    }
    .post_wrap .post_controls li.hide {
      display: none;
    }
    .post_wrap .post_controls a {
      color: #284B72;
      text-shadow: none !important;
    }
    .post_wrap .post_controls a:hover {
      color: #5176BC !important;
    }

    .post_block {
      border: 1px solid #072A66;
      margin: 6px;
    }

}
