style.css:
@-moz-document url-prefix("http://www.forocoches.com/") {
/* Oculto imágenes e iframes haciéndolos transparentes */
img,
iframe {
	opacity:0.05;
	filter:alpha(opacity=5);
}

/* Muestro imágenes e iframes al pasar el ratón por encima */
img:hover,
iframe:hover {
	opacity:1;
	filter:alpha(opacity=100);
    /* background: none; */
}

/* Excepciones: elementos */
img[src^="http://cdn.forocoches.com/foro/images"],
img[src^="//cdn.forocoches.com/foro/images"],
img[src^="http://st.forocoches.com/foro/images"],
img[src^="//st.forocoches.com/foro/images"],
img[src="clear.gif"],
#imagereg,
iframe#vB_Editor_QR_iframe,
iframe#vB_Editor_001_iframe,
iframe#vB_Editor_QE_1_iframe,
.shur-btn-options img
{
	opacity:1;
	filter:alpha(opacity=100)
}


 

