style.css:
/* 
Last Updated 6/14/2011 by Character_String 
Version 2.0.0
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

	body { /* page background */
		background-color: #EFEFE2 !important;
	}
	
	header, article, nav, section, footer, figure { /* Handles the color of all non-link text */
		color:  #000000;
	}
	
	
	header { /* page banner */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 0px 0px 10px 10px !important;
		text-align: center !important;
		font-style: italic;
		height: 40px;
		position: relative;
		top: -10px;
		/*[[header]]*/
	}
	
	/*[[font]]*/
	
	a { 	/* Text hyperlinks */
		color: #0000B2 !important;
	}
	a:visited { 
		color: #00007D !important;
	}
	a:hover { 
		color: #3333C1 !important; 
	}
	a:visited:hover { 
		color: #5C5CCD !important; 
	}

	.form-controls , #style-options fieldset { /* Left sidebar,style edit page and install options */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
	}

	#left-sidebar > * { /* skins the left sidebar with navi and related links */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 0px 0px !important;
		width: auto !important;
		height: 110px;
	}
	
	#related { /*overides #left-sider > * height to compensate for related styles being longer than the box*/
	height: auto !important;
	position: relative;
	top: -10px;
	border-top: none !important;
	border-radius: 0px 0px 10px 10px !important;
	}
	
	#search {
		position: relative;
		top: -168px;
		right: -20px;
	}
	
	#style_additional_info, #style_long_description, #css { /* controls the width and height of the edit style page */
		width: 90% !important;
	}
	
	article > ul { /* user page */
		background: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
		padding-top: 5px;
		padding-bottom: 5px;
		width: 350px;
	}
	
	#style-list { /* user page style list */
		background: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
		padding-left: 20px;
		padding-right: 5px;
		padding-top: 5px;
		padding-bottom: 5px;
		width: 85%;
		-moz-column-count:2;
		-moz-column-gap:40px;
		-moz-column-rule:2px outset #000000;
		-moz-column-rule-style:dotted;
	}
	
	#front-page-best { /* Front page "The best" links */
		background: #D4D4B2  !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 0px 0px 10px;
		padding-left: 4px;
		padding-right: 4px;
		width: 500px !important;
		border-right: none !important;
		position: relative;
		right: -10px;
	}
	
	 #front-page-newest { 	
		background: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 0px 10px 10px 0px;
		padding-left: 4px;
		padding-right: 4px;
		width: 500px !important;
		border-left: none !important;
	}
	
	h2 { /* changes all h2 text to italics */
		font-style: italic;
	}
	
	#social, .ad, #donate { /* Stuff that gets removed */
		display: none !important;
	}
	
	#additional-info { /* Stylesheet page */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px;
		padding-left: 3px;
		padding-right: 3px;
	}
	
	#miscellaneous-info { /* author, installs, etc */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px;
		padding-left: 3px;
		padding-right: 3px;
		padding-bottom: 3px;
		padding-top: 3px;
		width: 363px;
	}
	
	#control-panel { /* skins the edit and delete button on the style page */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px;
		padding-left: 7px;
		padding-right: 5px;
		padding-bottom: 5px;
		padding-top: 3px;
		width: 100px;
		position:relative;
		top: -5px;
	}
	
	#discussions-area { 	/* discussions area */
		background-color: #D4D4B2 !important; 
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px;
		width: 650px !important;
		position:relative;
		right: -17px;
	}
	
	#long-description { /* style author and long description */
		background-color: #D4D4B2 !important; 
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px;
		padding-top: 5px;
		padding-bottom: 5px;
		padding-left: 5px;
		padding-right: 5px;
		max-width: 750px;
	}
	
	#screenshots { /* screenshot area */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important; 
	}
	
	pre { /* Shows code block */
		background-color: #D4D4B2 !important; 
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px !important;
		overflow: auto;
		padding: 10px;
	}
	
	#install { /* Install box */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px !important;
		width: auto !important;
	}
	
	.more { /* Removes blue bar in left sidebar */
		border-top: 1px solid #D4D4B2 !important;
		padding-top: 2px !important;
	}
	
	footer { /* handles the "contact the admin" footer at the bottom of ever page */
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important; 
		border-radius: 10px 10px 10px 10px !important;
		width: 120px !important;
		padding-left: 8px !important;
	}

	/* This code adds a pop up code box. 
	
	This is edited code and was orginally made by luckymouse, so all credit goes to him */
	body {
		position: relative !important
	}
	
	#show-button {
		position: absolute !important;
		top: 0.3em !important ; right: 2em !important;
		z-index: 2 !important;
	}

	#view-code {

		overflow: auto !important;
		background-color: rgba(47,47,47,.9) !important;
		color: #ffffff !important;
		z-index: 1 !important;
		outline: 3000px solid rgba(0,0,0,.6)!important; /*pseudo background color*/
		position: fixed !important;
		width: 75%  !important;
		height: 85% !important;
		top: 0 !important;
		left: 0 !important;
		bottom: 0 !important; 
		right: 0 !important;
		margin: auto !important;}
		/* end of pop up code box code */
	}

	
/* affects the forum and forum releated pages only */
@-moz-document domain("forum.userstyles.org")

{
    body {
		background-color: #EFEFE2 !important;
		color: white !important;
	}
	
	.Menu, #Head {
		background-color: #D4D4B2 !important;
		text-align: center !important;
	}
	
	#Head {
		border-bottom: 2px solid #000000 !important;
	}
	
	.Title span, #Panel span {
		color: #0000B2 !important;
	}	

	div#UserOptions { /*skins the actual box with user options on the forum account page*/
		background: #D4D4B2 !important;
		color: #B1B1B1 !important;
		border-bottom: 1px solid #D4D4B2;
		padding: 2px 4px;
		text-align: right;
		position: relative;
		bottom: -20px;
		border-bottom: none !important;
		border-radius: 10px 10px 0px 0px !important;
	}
	
	ul.PanelInfo li { /*skins the content of the user option box on the forum account page*/
		background: #D4D4B2 !important;
		color: #B1B1B1 !important;
		border-bottom: 1px solid #D4D4B2;
		padding: 2px 4px;
		text-align: right;
		position: relative;
		border-bottom: none !important;
		border-radius: 10px 10px 0px 0px !important;
		padding-bottom: 5px !important;
	}

	.Item {
		background-color: #D4D4B2 !important;
		color: #B1B1B1 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
	}
	
	.Box {
		background-color: #D4D4B2 !important;
		color: #B1B1B1 !important;
		border: 2px solid #000000 !important;
		border-radius: 0px 0px 10px 10px !important;
		box-shadow: none !important;
	}
	
	div.Box h4 {
		color: #B1B1B1 !important;
	}
	
	.MyParticipated , .MyDrafts {
		background-color: #EFEFE2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
	}
	
	.Tabs, .MessageForm, .PreviewButton, .DraftButton, .WriteButton, #Form_Form, #UserOptions, .UserQuote {
		background-color: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
		padding-left: 5px !important;
		padding-top: 5px !important;
		padding-bottom: 5px !important;
	}
	
	#Form_Body {
		background: #EFEFE2 !important;
		color: #ffffff !important;
		border-radius: 10px 10px 10px 10px !important;
	}
	
	.Category {
		background: #D4D4B2 !important;
		box-shadow: 0 0 1px #D4D4B2 !important;
		border: 2px solid #000000 !important;
		text-shadow: none !important;
		border-radius: 10px 10px 10px 10px !important;
	}
	
	.Tabs.Active, code, .Attachments, .UserQuote {
		background-color: #EFEFE2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 10px 10px !important;
	}
	
	div.Search * { /*search bar*/
		background-color: #D4D4B2 !important;
	}
  
	a.Button, .Button, .InputBox {
		background: #D4D4B2 !important;
		box-shadow: 0 0 1px #D4D4B2 !important;
		border: 2px solid #000000 !important;
		color: #A30000!important;
		padding: 1px !important;
		text-shadow: none !important;
	}
   
	#Head h1 a span {
		text-shadow: none !important;
	}
   
	#Head a:hover {
		background: none repeat scroll 0 0 #EFEFE2 !important;
	}
   
	.BigButton.NewDiscussion {
		background: #D4D4B2 !important;
		border: 2px solid #000000 !important;
		border-radius: 10px 10px 0px 0px !important;
		box-shadow: none !important;
		text-align: center;
		text-shadow: none !important;
		position: relative;
		bottom: -12px;
		border-bottom: none !important;
	}	
 
	html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td { /* skins all non-link text */
		color: #000000;
	}
	
	ul.PanelInfo li, ul.PanelInfo li.Active, ul.PanelActivity li, ul.PanelInfo {
		border-top: none !important;
		border-bottom: none !important;
		box-shadow: none !important;
	}
   
   
   /* below code skins the user's forum account page */
	.Box.About {
		color: #ffffff !important;
		box-shadow: none !important;
	}
 
   
	div.User, .Meta { /*skins username on profile page */
		font-style: italic !important;
	}
	
		
	.QuoteAuthor {
		font-style: italic !Important;
		text-align: center !important;
	}	
	
	.ItemContent.Activity {
		padding-left: 10px !important;
	}
	
	.TextBox {
		background: #EFEFE2 !important;
		border-radius: 10px 10px 10px 10px !important;
	}
	
	span[style] {
		background: #EFEFE2 !important;
	}
	
	CODE > BR { /* fixes line break error caused when using <code> on the forums */
	display: none !important;
	}
	
	.Tabs.HeadingTabs.DiscussionTabs, .Tabs.DiscussionsTabs  {
		font-style: italic !important;
		position: relative;
		right:20px;
		bottom: 7px;
		width: 700px;
		height: 35px;
		font-size-adjust: 0.60; /*This line only works in Firefox. All it does is change the font size of the thread header.*/
	}
}
