style.css:
@-moz-document domain("emalls.ir") {
.prd-items .product-block-parent .product-block {
  padding: 0;
}
.prd-items .product-block-parent {
  width: 100%;
}
.prd-items .product-block-parent .prd-name {
  height: auto;
}
.prd-items .prd-ax {
  width: 55px;
  height: 55px;
  border-radius: 0;
}

.prd-items .product-block-parent .product-block,
.prd-items .prd-info {
  display: flex;
  flex-direction: row;
  align-items: center;
}
.prd-items .product-block-parent .product-block {
  gap: 0;
  padding: 0;
}
.prd-items .prd-info {
  padding: 0 0 0 5px;
  gap: 10px;
}
.prd-items .prd-info .price-box {
  width: 130px;
  justify-content: center;
}
.prd-price .from {
  display: none;
}
.prd-items .prd-info .btn-see {
  width: 140px;
  margin-top: 0;
}
.prd-items .prd-info .item-title {
  flex-basis: 80%;
  }
}
