style.css:
/**********************************/
/* Brazil              v1.2 skin  */
/* Date: 05/07/2011               */
/* Made by: Gizzly                */
/**********************************/

@-moz-document domain("hattrick.org") {

#page
{
	margin-top: 10px !important;
}
#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
    background: #000000 !important;
}
#footer a
{
        color: white!important;
        font-weight: bold !important;
}
#ticker
{
	position: absolute !important;
    width: 350px !important;
	top: 30px !important;
	left: 360px !important;
}
#ticker div a
{
	padding-bottom: 2px !important;
	color: black !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
	margin-top: 10px !important;
	margin-left:0px !important;
}
#teamLinks
{
    color: white !important;
    margin-top: 55px !important;
	margin-left: 180px !important;
}
div#teamLinks a
{
        color: white !important;
}
#ft_rapidid {  margin-top:-8px !important; }
#ft_rapidid a { color: white !important;}
#ft_rapidid_form {margin-top:-5px !important; }
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: white !important; margin-top: -8px !important;}
div[id="time"] {color: white !important; margin-top: -8px !important;}

/*--Page background-----------------------------------------------*/
body {background: black !important;}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: #001dcb !important;
        border-bottom: solid thin #001dcb!important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: #fddf0a !important;
        background-color: #001dcb !important;
        border-radius: 4px 4px 0 0 !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: #001dcb !important;
        background: none !important;
        border-bottom: solid 1px #001DCB !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/

.sidebarBox .boxHead .boxLeft, .subMenuBox .boxHead .boxLeft, .main .boxHead .boxLeft
{
        background: none !important;
}
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead { background: none !important;}
.sidebarBox .boxFooter .boxLeft,.subMenuBox .boxFooter .boxLeft,.main .boxFooter .boxLeft{ background: none !important; }
.sidebarBox .boxBody,.subMenuBox .boxBody,.main .boxBody{ border-left: none !important; border-right: none !important; }

/*Left menu*/
.subMenuBox .boxHead
{
        color: black !important;
	border-radius: 5px 5px 0 0 !important;
}
.subMenuBox .boxHead .boxLeft
{
        color: black !important;
        }
/*Centre box*/
.main .boxHead h2 
{
        color: black !important;
}
/*Right menu*/
.sidebarBox h2 
{
        color: #fddf0a !important;
}

.boxFooter
{
        background: white !important;
        border-radius: 0 0 4px 4px !important;
}
/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}
/*Central header paths*/
h2 a 
{
        color: #fddf0a !important;
}
h2 a:hover 
{
        color: white !important;
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: #001dcb !important;
        border-bottom: solid thin #001dcb !important;
}
/*General Links*/
a
{
    color: #001dcb !important;
	/*text-decoration: none !important;*/
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
    /*text-decoration: none !important;*/
	color: black !important;
}
.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}
/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
	padding-top: 15px !important;
    background: #fbde0e !important; /* Old browsers */
	background: -moz-linear-gradient(top, #fbde0e 0%, #fbde0e 58%, #001dcb 100%) !important; /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fbde0e), color-stop(58%,#fbde0e), color-stop(100%,#001dcb)) !important; /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top, #fbde0e 0%,#fbde0e 58%,#001dcb 100%) !important; /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top, #fbde0e 0%,#fbde0e 58%,#001dcb 100%) !important; /* Opera11.10+ */
	background: -ms-linear-gradient(top, #fbde0e 0%,#fbde0e 58%,#001dcb 100%) !important; /* IE10+ */
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fbde0e', endColorstr='#001dcb',GradientType=0 ) !important; /* IE6-9 */
	background: linear-gradient(top, #fbde0e 0%,#fbde0e 58%,#001dcb 100%) !important; /* W3C */
}
/*Image header (banner)*/
#header 
{
	height: 142px !important;
	/*padding-top: 20px !important;*/
	background: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/Brazil/header.png") repeat scroll 0 -15px transparent  !important;
	border-radius: 20px 20px 0 0 !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	height: 20px !important;
	width: 981px !important;
	margin-top: 5px !important;
	margin-left: -14px !important;
	margin-bottom: -5px !important;
	padding-left: 0px !important;
	background: #001dcb!important;
	position: absolute !important;
}
#menu a
{
	line-height: 20px !important;  
    color: #fddf0a!important;
}
div#menu a:hover 
{
        color: #001dcb!important; 
        background: #fddf0a!important;
}
a:hover
{
	color: black !important;
}
/*bottom white line*/
div[id="bottom"]
{
    background: #001dcb !important;
	border-radius: 0 0 20px 20px !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"] a:hover {color: #001dcb !important;}

/*---Forum---------------------------------------------------------------*/
div[class="boxBody"]   {background: white !important;}
div[class="message"] a {color: #001dcb !important;}
div[class="signature"] a {color: #001dcb !important;}

.cfHeader
{
	background: none repeat scroll 0 0 #001dcb !important;
}
#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #ffffff;
}
.cfWrapper
{
	border: solid 1px #001dcb !important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: #fddf0a !important;
}
.cfUserInfo a
{
        color: #001dcb !important;
}
.folderHeader a:link 
{
	color: #D11E1A !important;
	font-weight: bold !important;
}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background: #ffffff !important;
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/

/*------Foxtrick popup-------*/
ul.ft-popup-list {background-color: black !important; border: solid 1px white !important; }
ul.ft-popup-list > li > a,  ul.ft-popup-list > li > span {	color: white !important; }
ul.ft-popup-list > li > a:hover {color: #001dcb !important; background-color: #fddf0a !important; }
ul.ft-popup-list > li:hover {background-color: #fddf0a !important;}

/*------lineup-order-----*/
#order_tabs a {color:#fddf0a !important;}
#order_tabs a:hover {color:#001dcb !important;}

/*------MyOffice---------*/
.eventCalendarDate.selectedEventDate {background-color:#fddf0a !important; border-color: #001dcb !important; color: #001dcb !important;}
.eventItem { background-color:#fddf0a !important; border:1px solid #001dcb !important;}
.eventItem > .eventItemText {color: #001dcb !important;}


.alert, .edit, .kitEdit, .alert_top  {border: 1px solid #001dcb !important; }
/*shortcuts*/
img.scNewMail 
{

}
}
