style.css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document url-prefix(http://www.insanejournal.com/portal/) {
 /* IJ portal blues */
 /* 3rd revision, 2008 April 05 */
 /* works fine in New Tweak, Insanime, Honeycomb Redesigned, Honeycomb, Hand, Trails, Brown, Clean, Lynx, both Worksafes and both Dramatics */
 /* InsaneLook and Blue White suck even for default portal, sorry! */
 .PortalBoxIcon {display:inline !important;}
 .DropDownMenuContent .PortalMenuItem .PortalBoxIcon {display:none !important;}
 body .PortalBoxContent, body #PortalContentContainer .NewUser table td { /* color:#333 !important; */ background-color:#eaeaea !important;}
 body #PortalContentContainer, body #PortalContentContainer .NewUser table td {color:#333 !important;}
 body #PortalContentContainer .UpdateJournal table td, .PortalBoxTitleText {color:#fff !important;}
 /* links */
 div.PortalBoxContent a {color:#415680 !important;}
 div.PortalBoxContent a:visited {color:#27324A !important;}
 div.PortalBoxContent a:hover, body div.PortalBoxContent a:focus {border-bottom: 1px solid #415680 !important; color:#415680 !important;}
 /* left borders on Manage links */
 .Manage table a {border-left-color:#27324A !important;}
 /* portal module headers */
 /* table.menu, */ #PortalConfigMenu, .PortalMenuItem, .PortalBoxTitleBar, /* .PortalBoxTitleText, */ .PortalPatternedTitleBar, .UpdateJournal table {background-color:#415680 !important; color:#fff !important; background-image:url("data:image/gif;base64,R0lGODlhAQAbAIQYADVFZzVGaDVHaDVIajZIajdIazdKbDhKbjhLbjlLcDpMcTtNcztOdDtOdTxPdjxQdz1ReD5ReD5Sej9Tej9TfD9UfEBVfkFVf0FWgEFWgEFWgEFWgEFWgEFWgEFWgEFWgCH+FUNyZWF0ZWQgd2l0aCBUaGUgR0lNUAAh+QQBCgAfACwAAAAAAQAbAAAFFiCGWZQEOYySHMUQAAJhIMrSPNFUXSEAOw==") !important;}
 #PortalConfigMenu, .UpdateJournal table {background-image:none !important;}
 /* cleanup and restoration */
 /* keep white plus sign */
 #AddPortalMenuButton {background-image:url(http://pics.livejournal.com/veroz/pic/00051xwq) !important;}
 /* don't overlay the same background over itself */
 .PortalMenuItem .PortalMenuItem, #AddPortalMenuButton .PortalMenuItem, #PortalConfigMenu .PortalMenuItem,  body #PortalContentContainer .UpdateJournal table td {background-image:none !important; background-color:transparent !important;}
 /* hide the float clearer in Honeycomb Redesigned */
 body #PortalContentContainer .clearing  {clear:both;}
 /* secondary background colour (update table/inbox/news) */
 .PortalTableHeader td {background-color:#60A0DC !important;}

 /* box up modules and their editing popups */
 .PortalBox, .PortalBoxConfig {background-color:#eaeaea !important; border: 1px solid #415680 !important;}
 .UpdateJournal table {color:#fff !important;}
 .PortalBoxTitleBar, .PortalBoxTitleText {margin:0 !important;}
}
