.sideblock .content {
   /* rounded edges in sidebar */
   -moz-border-radius-bottomright: 20px !important ;
   -moz-border-radius-bottomleft: 20px !important;
}

div.header {
   /* left sidebar header-color */
   background-color: #333399 !important;
}

#right-column .header {
   /*right sidebar header-color */
   background: #15853a !important;
   color: #ffffff !important;
}

h2.main a{
   /*headlines in home-section*/
   color: #333399 !important;
}

#middle-column h2.headingblock {
   /*headlines in courses*/
   color: #333399 !important;
   font-family: Arial !important;
}

a:hover {
   /*every link-hover */
   color: #15853a !important;
}

}
