style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("jira.atlassian.com"),
domain("support.tais.ru")
{


/* ===================================================== */
/* Compact issue tables */
/* ===================================================== */

#issuetable td.nav {
    line-height: 1 !important;
    padding: 0px 10px 0px 0px !important;
    vertical-align: middle !important;
    white-space: nowrap !important;
}

div.gadget #issuetable td.nav {
    white-space: normal !important;
    padding: 1px 0.3em 0px 0px !important;
}

div.gadget #issuetable td.status {
    white-space: nowrap !important;
}

#issuetable td.nav p {
    overflow: hidden !important;
    height: 12px !important;
}

#issuetable td.status {
  font-size: 0px !important;
  width: 16px !important;
}

#issuetable tr.rowHeader th {
  padding:0px 0.5em 0px 0px !important;
}

/* ===================================================== */


/* ===================================================== */
/* Custom assignee coloring */
/* ===================================================== */

#issuetable td.assignee a#assignee_zuber {
    color: blue !important;
}

#issuetable td.assignee a#assignee_tty01 {
    color: green !important;
}

#issuetable td.assignee a#assignee_perelucci {
    color: gray !important;
}

#issuetable td.assignee a#assignee_zy {
    color: silver !important;
}

/* ===================================================== */


/* ===================================================== */
/* test on jira.atlassian.com */
/* ===================================================== */

#issuetable td.assignee a#assignee_jlargman {
    color: green !important;
}
#issuetable td.assignee a#assignee_npellow {
    color: blue !important;
}

/* ===================================================== */


}
