style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("twitch.tv"), domain("twitchtv.com"){

/*[[Resolution]]*/

/* [Ads Boxes] */
#ad {display:none !important;}
.a300, .managed_ad, .advertisement, .hide_ad small {display:none !important;}
.ad_leader dir_leader{display:none !important;}
#Twitch_DirLeaderv2_holder{display:none !important;}
#Twitch_DiropaBanner_holder{display:none !important;}
#facebook_logging_in {display:none !important;}
#facebook_cancelled {display:none !important;}
#adaptv_ad_player_div {display:none !important;}
#chat_redisplay_holder {display:none !important;}

}

options.value:
/* [Main Column Size]*/
.main, .main.discovery {width: 1280px !important; margin-top: 10px !important; padding-top: 10px!important;}
.dark_wrapper {width: 950px !important;}
#site_header {height: 45px !important;}
#header_logo {margin-top:5px !important; margin-bottom: 0px !important;}

/* [Dashboard] */
.wrapper.c12.clearfix {width: 1270px !important;}
.wrapper.c9, .wrapper.c9.clearfix {width: 1260px !important;}
#videos_column {width: 940px !important;}
#dash_main {width: 1280px !important; padding-left: 0px !important; padding-right: 0px !important;}
.static_header{width: 1270px !important; margin-left:0px !important; margin-right: 0px !important; padding-left: 0px !important; padding-right: 0px !important;}
#controls_column{width:360px !important; margin-right:0px !important;}

/* [Directory Channels Column Size] */
.wrapper {width: 1270px !important; padding-left: 10px !important; padding-right: 10px !important;}
.grid.c4 {margin-right: 10px !important;}
.grid.c8.last, list-streams, .streams-grid {width: 960px !important; margin-right: 0px !important;}
.stream.clearfix, .stream{width: 220px !important; margin-bottom: 20px !important;}
.game, .game.clearfix{margin-bottom: 10px !important;}
.thumb > img{width: 220px !important;}
.thumb {margin-bottom: 10px !important;}
#fb_like_button{margin-bottom:10px !important;}

/* [Player Column Size]*/
#player_column{width: 910px !important;float: left !important; margin-right: 10px !important;}
#broadcast_meta{height: auto !important; max-width: 635px !important; margin-bottom:10px !important;}
#broadcast_meta > h2 {height: 50px !important; overflow: hidden; font-size:1.6em !important;}
#broadcast_title {margin-bottom:5px !important;}
#header_banner{
  float: right;
  height: 50px !important;
  width: 255px!important;
  max-height: 100% !important;
  max-width: 100% !important;
  padding-top: 0px !important;
  margin-right: 10px !important;
  margin-bottom: 0px !important;}
#header_banner > img{
  height: inherit !important;
  width: inherit !important;
  max-height: inherit !important;
  max-width: inherit !important;}

/*[Flash Player Size]*/
#standard_holder{width: 910px !important;height: 540px !important;display: block !important;}
#live_site_player_flash {width: 910px !important;height: 540px !important;}
.archive_site_player_container.swf_container,#archive_site_player_flash {width: 910px !important;height: 540px !important;}

/* [Chat] */
#twitch_chat, #chat_column {height: 600px !important; width: 340px !important; margin-left:0px !important; margin-top: 0px !important;}
#chat {margin-bottom: 50px !important;}
#chat_lines {height: inherit !important; overflow:auto !important; overflow-x:hidden !important; margin-top: 0px !important;}
#chat_line_list {font: 1.05em/1.5"Lucida Grande","Lucida Sans Unicode","Lucida Sans",Geneva,Verdana,sans-serif !important;}

